/**
 * Plurishing-backend
 * =======
 * Assets component controller
 */
import {lookup} from 'mime-types';
import {v4 as generateId} from 'uuid';
import formidable from 'formidable';
import {readFile, remove} from 'fs-extra';
import {resolve} from 'path';

import {
  getAssets as getAssetsDAL,
  getAsset as getAssetDAL,
  getAssetAttachment as getAssetAttachmentDAL,
  createAsset as createAssetDAL,
  updateAsset as updateAssetDAL,
  deleteAsset as deleteAssetDAL
} from './assets.dal';

/**
 * Get a list of assets
 * @param {object} req - the request of query
 * @param {object} res - the resource of query
 * @todo add filters handling through req queries
 */
export const getAssets = (req, res) =>
  res.json(getAssetsDAL({}));

/**
 * Get a single asset
 * @param {object} req - the request of query
 * @param {object} res - the resource of query
 */
export const getAsset = (req, res) =>
  res.json(
    getAssetDAL({
      id: req.params.id
    })
  );

/**
 * Get a single asset attachment
 * @param {object} req - the request of query
 * @param {object} res - the resource of query
 */
export const getAssetAttachment = (req, res) => {
  getAssetAttachmentDAL({
    ...req.query,
    id: req.params.id,
    filename: req.params.filename
  })
  .then(({data, mimetype}) => {
    res.writeHead(200, {'Content-Type': mimetype});
    res.end(data, 'binary');
  });
};

/**
 * Create a single asset
 * @param {object} req - the request of query
 * @param {object} res - the resource of query
 */
export const createAsset = (req, res) => {
  const filename = decodeURIComponent(req.params.id);
  const form = new formidable.IncomingForm({
    uploadDir : resolve(__dirname + '/../../../temp'),
    // multiples : true,
    keepExtensions : true
  });

  form.on('error', function(err){
    return res.status(500).send(err);
  });


  form.parse(req, function(err, fields, files) {
    const attachmentPath = files.file.path;
    readFile(attachmentPath)
      .then(data =>
        createAssetDAL({
          _id: generateId(),
          filename,
          type: 'asset'
        }, data)
      )
      .then(() => remove(attachmentPath))
      .then(() => res.json(getAssetsDAL({})))
      .catch(e => res.status(500).send(e));
  });
};
/**
 * Update a single asset
 * @param {object} req - the request of query
 * @param {object} res - the resource of query
 */
export const updateAsset = (req, res) => {
  const assetId = req.params.id;
  const form = new formidable.IncomingForm({
    uploadDir : resolve(__dirname + '/../../../temp'),
    // multiples : true,
    keepExtensions : true
  });

  form.on('error', function(err){
    return res.status(500).send(err);
  });


  form.parse(req, function(err, fields, files) {
    const attachmentPath = files.file.path;
    const filename= files.file.name;
    const mimetype = lookup(filename);
    readFile(attachmentPath)
      .then(data =>
        updateAssetDAL(assetId, {
          filename,
          mimetype
        }, data)
      )
      .then(() => remove(attachmentPath))
      .then(() =>
        res.json(
          getAssetDAL({
            id: assetId
          })
        )
      )
      .catch(e => res.status(500).send(e));
  });
};

/**
 * Delete a single asset
 * @param {object} req - the request of query
 * @param {object} res - the resource of query
 */
export const deleteAsset = (req, res) =>
  res.json(deleteAssetDAL({_id: req.params.id}));

/**
 * Plurishing-backend
 * =======
 * Asset data access layer
 */
import {lookup} from 'mime-types';

import db from '../../utils/plugDb';

import {Asset as schema} from 'plurishing-schemas';

import design from './assets.design';
import {addDesign} from '../../utils/couchdb';
import {dataIsValid} from '../../utils/schemas';

/**
 * Registering couchdb views related to assets objects
 */
addDesign(db, 'assets', design);


/**
 * Gets a list of assets with optional query params
 * @param {object} parameters
 * @return {Promise}
 */
export const getAssets = ({
  query = {},
  include_docs = true
}) => {
  if (query.filename) {
    return db.viewAsync('assets', 'by_filename', {
      key: query.filename,
      include_docs
    }).then(
      results =>
        new Promise(
          resolve =>
            resolve(
              results.rows.map(doc => doc.doc)
            )
          )
    );
  } else {
    return db.viewAsync('assets', 'by_filename', {
      include_docs
    }).then(
      results =>
        new Promise(
          resolve =>
            resolve(
              results.rows.map(doc => doc.doc)
            )
          )
    );
  }
};

/**
 * Gets a asset with a specific id or email
 * @param {object} parameters
 * @return {Promise}
 */
export const getAsset = ({
  id
}) =>
  db.getAsync(id)
    .then(asset =>
      // verify that data is compliant before returning it
      dataIsValid(asset, schema)
    );

/**
 * Gets a asset with a specific id or filename
 * @param {object} parameters
 * @return {Promise}
 */
export const getAssetAttachment = ({
  id,
  filename,
  encoding
}) =>
  new Promise((resolve, reject) => {
    db.attachment.get(id, filename, (err, body) => {
      if (err) {
        reject(err);
      } else {
        const content = encoding === 'base64' ?
          Buffer.from(body, 'binary').toString('base64')
          : body;
        resolve({data: content, mimetype: lookup(filename)});
      }
    });
  });


/**
 * Creates a new asset
 * @param {object} asset
 * @param {object} data
 * @return {Promise}
 */
export const createAsset = (asset, data) => {
  const {filename} = asset;
  const mimeType = lookup(filename) || 'application/octet-stream';
  // validate data
  return dataIsValid(asset, schema)
  // create document
  .then(() =>
    db.insertAsync({
      ...asset,
      mimetype: mimeType
    })
  )
  // add attachment
  .then(op => {
    const {id, rev} = op;

    return new Promise((resolve, reject) => {
      db.attachment.insert(
        // doc id
        id,
        // attachment filename
        filename,
        // attachment data
        data,
        // doc mimetype
        mimeType,
        // doc rev
        {rev},
        // callback
        (err, body) => {
          if (err) {
            reject(err);
          } else {
            resolve(body);
          }
        }
      );
    });
  });
};

/**
 * Updates an asset
 * @param {string} id - id of the asset
 * @param {object} asset - asset data to update
 * @param {object} data - attachment data to update
 * @return {Promise}
 */
export const updateAsset = (assetId, {filename, mimetype}, attachmentData) => {
  // get existing document
  return db.getAsync(assetId)
          // then update document
          .then(asset =>
            db.insertAsync({
              ...asset,
              filename,
              mimetype,
              rev: asset._rev
            })
          )
          // destroy previous attachments
          .then(() =>
            db.getAsync(assetId)
          )
          .then(asset => {
            const previousAttachment =  asset._attachments && Object.keys(asset._attachments)[0];
            return new Promise((resolve, reject) => {
              if (previousAttachment) {
                db.attachment.destroy(asset._id, encodeURIComponent(previousAttachment), {rev: asset._rev},
                  (err, body) => {
                    if (err) {
                      reject(err);
                    } else resolve(body);
                  });
              }
            });
          })
          // add new attachment
          .then(op => {
            const {id, rev} = op;

            return new Promise((resolve, reject) => {
              db.attachment.insert(
                // doc id
                id,
                // attachment filename
                filename,
                // attachment data
                attachmentData,
                // doc mimetype
                mimetype,
                // doc rev
                {rev},
                // callback
                (err, body) => {
                  if (err) {
                    reject(err);
                  } else {
                    resolve(body);
                  }
                }
              );
            });
          });
};

/**
 * Deletes a asset
 * @param {object} asset - asset to delete
 * @return {Promise}
 */
export const deleteAsset = ({_id}) =>
  db.getAsync(_id)
    .then(asset => db.destroyAsync(_id, asset._rev));

export default {
  "views": {
    "by_filename": {
      "map": `function(doc) {
                    if (doc.type === 'asset') {
                      emit(doc.filename, null);
                    }
                  }`
    }
  }
};

