        sendEmail({
          from,
          to, // array
          subject,
          html
        }, {
          smtpEmail: smtp_email,
          smtpService: smtp_service,
          smtpPassword: smtp_password
        }, console)/* eslint no-console : 0 */
        .then(resolve)
        .catch(reject);
    });
}

