                  })
                  .then(({data}) => {
                    return writeFile(`${tempAssetsFolder}/${asset._id}/${asset.filename}`, data);
                  });
              });
              return Promise.all(toResolve);
            })
            .then(() => {
              const getAssetUri = asset => `${tempAssetsFolder}/${asset._id}/${asset.filename}`;
              resolve(getAssetUri);
            })
            .catch(reject);
  });

