import Ajv from 'ajv';
const ajv = new Ajv();

export const dataIsValid = (data, schema) =>
  new Promise((resolve, reject) => {
    const valid = ajv.validate(schema, data);
    if (valid) {
      resolve(data);
    } else {
      reject(ajv.errors);
    }
  });

/**
 * Multimodal Publishing utils
 * ===========================
 * transformJSON
 * ===========================
 * This module resolves a data object by transforming it
 * from an input schema to an output schema using
 * a transformation function.
 * @module mpu/helpers/transformJSON
 */
import Ajv from 'ajv';
const ajv = new Ajv();

/**
 * Retrieves the arguments name of a function
 * @param {function} func - the function to analyse
 * @return {array} arguments - the arguments names
 */
function $args(func) {
    return (func + '')
      .replace(/[/][/].*$/mg,'') // strip single-line comments
      .replace(/\s+/g, '') // strip white space
      .replace(/[/][*][^/*]*[*][/]/g, '') // strip multi-line comments
      .split('){', 1)[0].replace(/^[^(]*[(]/, '') // extract the parameters
      .replace(/=[^,]+/g, '') // strip any ES6 defaults
      .split(',').filter(Boolean); // split & filter [""]
}

/**
 * @param {object|Array} inputData - the original data to convert
 * @param {object} inputSchema - the schema of original data to convert in json-schema
 * @param {object} outputSchema - the schema of the resulting data to convert in json-schema
 * @param {function} transformFunction - a function taking data as argument and returning new data
 * This transform function can :
 * - return a json object resolving with a json object
 * - return a promise resolving with a json object
 * - provide a node-js style callback as second argument
 * @return {Promise} promise
 */
const transformJSON = (
    inputData,
    inputSchema,
    outputSchema,
    transformFunction,
) =>
    new Promise(
        (resolve, reject) => {
          try{
            // verify input is valid
            const inputIsValid = ajv.validate(inputSchema, inputData);
            if (!inputIsValid) {
              return reject(ajv.errors);
            }
            // check if transform function presents a callback
            if ($args(transformFunction).length > 1) {
              // resolve with callback
              return transformFunction(inputData, (err, output) => {
                if (err) {
                  return reject(err);
                } else {
                  // apply transform function to input
                  const outputIsValid = ajv.validate(outputSchema, output);
                  if (!outputIsValid) {
                    reject(ajv.errors);
                  } else {
                    resolve(output);
                  }
                }
              });
            }
            // apply transform function to input
            const outputTemp = transformFunction(inputData);
            // if the function has returned a promise resolve it
            if (outputTemp instanceof Promise) {
              outputTemp
                .then(output => {
                  // verify that output complies to output schema
                  const outputIsValid = ajv.validate(outputSchema, output);
                  if (outputIsValid) {
                    resolve(output);
                  } else {
                    // @todo normalize error response
                    reject(new Error());
                  }
                })
                .catch(e => {
                  reject(e);
                });
            } else {
              // verify output complies to output schema
              const outputIsValid = ajv.validate(outputSchema, outputTemp);
              if (outputIsValid) {
                resolve(outputTemp);
              } else {
                // @todo normalize error response
                reject(new Error());
              }
            }
          } catch(e) {
            reject(e);
          }
        }
    );

export default transformJSON;

import {expect} from 'chai';
import {describe, it} from 'mocha';

// tested function
import transformJSON from './transformJSON';

describe('transformJSON util', () => {

  // mock data
  const inputData = {
    foo: 'hello'
  };
  const expectedOutput = {
    bar: 'hello'
  };

  // input json schema
  const inputSchema = {
    properties: {
      foo: {
        type: 'string'
      }
    },
    required: ['foo']
  };

  // output json schema
  const outputSchema = {
    properties: {
      bar: {
        type: 'string'
      }
    },
    required: ['bar']
  };

  // transform function
  const transform = input => {
    const output = {...input};
    const bar = input.foo;
    delete output.foo;
    output.bar = bar;
    return output;
  };
  // transform function as a promise
  const transformAsPromise = input =>
    new Promise((resolve) => {
      const output = transform(input);
      resolve(output);
    });

  // transform function with callback
  const transformWithCallback = (input, callback) => {
    const output = transform(input);
    callback(null, output);
  };

  it('should return a promise', done => {
    const fn = transformJSON(inputData, inputSchema, outputSchema, transform);
    expect(fn).to.be.an.instanceof(Promise);
    done();
  });
  it('should apply the transform function to input data and resolve with correctly transformed data', done => {
    transformJSON(inputData, inputSchema, outputSchema, transform)
      .then(output => {
        expect(output).to.deep.equal(expectedOutput);
        done();
      })
      .catch();
  });
  it('should accept a transform function returning a promise', done => {
    transformJSON(inputData, inputSchema, outputSchema, transformAsPromise)
      .then(output => {
        expect(output).to.deep.equal(expectedOutput);
        done();
      })
      .catch();
  });

  it('should accept a transform function presenting a callback', done => {
    transformJSON(inputData, inputSchema, outputSchema, transformWithCallback)
      .then(output => {
        expect(output).to.deep.equal(expectedOutput);
        done();
      })
      .catch();
  });

  it('should require inputData parameter to be a js object', done => {
    transformJSON(undefined, inputSchema, outputSchema, transform)
      .catch(e => {
        expect(e).to.be.an('error');
        done();
      });
  });

  it('should require inputSchema parameter to be a valid json schema', done => {
    transformJSON(inputData, undefined, outputSchema, transform)
      .catch(e => {
        expect(e).to.be.an('error');
        done();
      });
  });

  it('should require outputSchema parameter to be a valid json schema', done => {
    transformJSON(inputData, inputSchema, undefined, transform)
      .catch(e => {
        expect(e).to.be.an('error');
        done();
      });
  });

  it('should reject an input data not complying to input json schema', done => {
    transformJSON({bar: 'hello'}, inputSchema, outputSchema, transform)
      .catch(e => {
        expect(e).to.be.an('array');
        done();
      });
  });

  it('should reject an output data not complying to output json schema', done => {
    const wrongTransform = input => input;
    transformJSON({bar: 'hello'}, inputSchema, outputSchema, wrongTransform)
      .catch(e => {
        expect(e).to.be.an('array');
        done();
      });
  });

});
