 * Updates a user
 * @param {string} id - id of user to update
 * @param {object} user - data to update
 * @return {Promise}
 */
export const updateUser = (id, user) =>
  // validate
  dataIsValid(user, schema)
    // get existing document
    .then(() => db.getAsync(id))
    // update with new rev
    .then(userDb =>
        db.insertAsync({
          ...user,
          _rev: userDb._rev
        }, userDb._id)
    );

/**
 * Deletes a user
 * @param {object} user - user to delete
 * @return {Promise}
 */
